export function textFly(anime){
  const textFly = document.querySelector(".fly__text")
  // разбивает каждую букву на span, важно!!!!!!!!!!!!!!!!!!!!!!
  textFly.innerHTML = textFly.textContent.replace(/\S/g, "<span>$&</span>")

  const section26Height = document.querySelector(".section26").clientHeight / 2

  const animation = anime.timeline({
    targets: ".fly__text span",
    easing: "easeInOutExpo",
    loop: true,
  })

  animation
    .add({
      rotate: function(){
        return anime.random(-360, 360)
      },
      translateX: function(){
        return anime.random(-500, 500)
      },
      translateY: function(){
        return anime.random(-section26Height, section26Height)
      },
      duration: 5000,
      delay: anime.stagger(20)
    })


    .add({
      rotate: 0,
      translateX: 0,
      translateY: 0,
      duration: 5000,
      delay: anime.stagger(20)
    })
}